import React, { Component } from 'react'
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './fonts/Lato-Regular.ttf';
import './game';

class gamePage extends Component{
  constructor(props){
    super(props)

    this.state = {
      input: "",
      word: null,
      time: 5,
      score: 0,
      isPlaying: false,
      message: ""
    };
  }

  handleChange = (e) => {
    this.setState({input: e.target.value});
    console.log(this.state);

    let current_word = this.state.word;
    let input = e.target.value;
    console.log(input);

  }

  render() {
    return (
      <React.Fragment>
        <div className="App">
          <header class="p-3 mb-5">
            <h1 class="page-text name">Type Fast</h1>
          </header>
          <div class="row">
            <div class="col-md-6 mx-auto">
              <h2 class="display-2 mb-5 page-text" id="current-word">hello</h2>
              <input type="text" class="form-control form-control-lg page-text word-input" placeholder="Start typing..." id="word-input"
                  autofocus onChange={(e) => {this.handleChange(e)}}></input>
              <h4 class="mt-3 page-text" id="message"></h4>

              <div class="row mt-5">
                  <div class="col-md-6">
                      <h3 class="page-text">Time Left:
                          <span id="time">0</span>
                      </h3>
                  </div>
                  <div class="col-md-6">
                      <h3 class="page-text">Score:
                          <span id="score">0</span>
                      </h3>
                  </div>
                  <div class="col">
                      <h3 class="page-text">High Score:
                          <span id="highscore">0</span>
                      </h3>
                  </div>
              </div>

              <div class="row mt-5">
                  <div class="col-md-12">
                      <div class="card-body">
                          <h5 class="page-text">Instructions</h5>
                          <p class="page-text">Type each word or character in the given amount of seconds to score.
                              Begin typing to start playing.
                          </p>
                      </div>
                  </div>
              </div>
            </div>
          </div>
        </div>

      </React.Fragment>
    );
  }
}


export default gamePage;
